{
  "name": "User Data Form",
  "description": "A sample form to capture user data",
  "creator": "Anonemous2",
  "version": "1.0",
  "page_count": 2,
  "form_timeout": 30,
  "pages": [
    {
      "name": "Page 1",
      "fields": [
        {
          "id": "name",
          "name": "Name",
          "type": "text",
          "required": true,
          "placeholder": "Enter your name here",
          "metadata": {
            "max_length": 50,
            "min_length": 5,
            "pattern": "^[a-zA-Z ]+$",
            "styles": {
              "width": "50%"
            }
          }
        },
        {
          "id": "gender",
          "name": "Gender",
          "type": "select",
          "required": true,
          "options": ["M", "F", "Non-Binary"],
          "metadata": {
            "styles": {
              "width": "50%"
            }
          }
        },
        {
          "id": "age",
          "name": "Age",
          "type": "number",
          "required": true,
          "placeholder": "Enter your age here",
          "metadata": {
            "min": 13,
            "max": 200,
            "styles": {
              "width": "50%"
            }
          }
        },
        {
          "id": "next",
          "name": "Next",
          "type": "buttom",
          "required": true,
          "metadata": {
            "action": "next_page",
            "styles": {
              "width": "50%"
            }
          }
        }
      ]
    },
    {
      "name": "Page 2",
      "fields": [
        {
          "id": "profession",
          "name": "Profession",
          "type": "select",
          "required": true,
          "options": ["Owner", "Agent", "Buyer", "Seller", "Other"],
          "metadata": {
            "styles": {
              "width": "50%"
            }
          }
        },
        {
          "id": "services",
          "name": "Services",
          "type": "text",
          "required": false,
          "placeholder": "Enter the services you need",
          "metadata": {
            "max_length": 100,
            "styles": {
              "width": "50%"
            }
          }
        },
        {
          "id": "submit",
          "name": "Submit",
          "type": "buttom",
          "required": true,
          "metadata": {
            "action": "submit_form",
            "styles": {
              "width": "50%"
            }
          }
        }
      ]
    }
  ]
}

